package dynamicProgramming;

import java.util.HashMap;
import java.util.Map;

public class LongestCommonSubsequence {
	static Map<String,Integer> lookup;
	static int [][]dp;
 	static int longestCommonSubsequenceHelper(String text1, String text2,int idx1,int idx2) {
		if (text1.length() == idx1 || text2.length() == idx2) {
			return 0;
		}
		if(dp[idx1][idx2]==0) {
			if (text1.charAt(idx1) == text2.charAt(idx2)) {
				
				int ans= 1 + (longestCommonSubsequenceHelper(text1,
						text2,idx1+1,idx2+1));
//				lookup.put(text1+"|"+text2,ans);
				dp[idx1][idx2] =ans; 
				return ans;
			} else {
				int ans= Integer.max(longestCommonSubsequenceHelper(text1, text2,idx1+1,idx2),
						longestCommonSubsequenceHelper(text1, text2,idx1,idx2+1));
//				lookup.put(text1+"|"+text2,ans);
				dp[idx1][idx2] =ans; 
				return ans;
			}
		}else {
//			return lookup.get(text1+"|"+text2);
			return dp[idx1][idx2];
		}
		
	}

	public static void main(String[] args) {
		dp = new int[1000][1000];

		long startTime = System.nanoTime();
	
		
		
		System.out.println(longestCommonSubsequenceHelper("",
				"",0,0));
		long endTime = System.nanoTime();

		long duration = (endTime - startTime);
		System.out.println(duration / 1000000);
	}

}
